include '../../../config/bootstrap.php';

//This is an ajax request
if(!isset($_SERVER['HTTP_X_REQUESTED_WITH']) 
&& strtolower($_SERVER['HTTP_X_REQUESTED_WITH']) != 'xmlhttprequest')
{
	die();
}

$id_product = intval($_POST['id_product']);
$attributes = json_decode($_POST['attributes'], true);
if( empty($attributes) || $id_product < 1 )
	return false;

use Core\Controllers\Front\ProductController;

$product = new ProductController();
$combinations = $product->getCombinations($id_product, $attributes);
//var_dump($combinations->id );
if( isset($combinations->id) ){
	$return['comb'] = json_encode($combinations);
} else {
	$return['empty'] = trans("This product is no longer in stock with those attributes but is available with others.", "core");
}

echo json_encode($return);